import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-nav-list',
  templateUrl: './nav-list.component.html',
  styleUrls: ['./nav-list.component.css']
})
export class NavListComponent {
  @Input() pag : { offset: number, limit: number, current: number, total: number } = {offset:0,limit:20,current:1,total:0};
  public totalPags : number[] = [];
  @Input() view : boolean = false;
  @Output() pagination = new EventEmitter<boolean>();
  @Output() darkEmision= new EventEmitter<boolean>();
  @Output() listFavourites = new EventEmitter<boolean>();
  @Output() sendview = new EventEmitter<boolean>();
  public fav : boolean = false;
  public dark : boolean = true;

  public switchViews() : void
  {
    this.view = !this.view;
    this.sendview.emit();
  }
  public listFav() : void
  {
    this.fav = !this.fav;
    this.listFavourites.emit(this.fav)
  }

  public isNext(bool:boolean) : void
  {
    if(!this.fav && this.pag.current > 0){
      this.pagination.emit(bool);
    }
  }
  public darkMode(bool : boolean) : void
  {
    this.dark = !this.dark;
    this.darkEmision.emit(bool);
  }

  ngOnInit(){
    const data = localStorage.getItem('info');
    this.dark = data ? !JSON.parse(data).dark : true;
  }

}
<div class="row">
  <div class="col-sm-6">
    <nav>
      <ul class="pagination pagination-sm">
        <div class="{{fav?'': 'animated'}}{{pag.current <= 1?'animated': ''}}">
          <li class="page-item">
            <span (click)="isNext(false)" [routerLink]="['/list/'+(pag.current-1).toString()]" class="page-link arrow-button arrow{{!fav?'': 'disabled'}}{{pag.current <= 1?'disabled': ''}} m-2"><img class="arrow-nav"
                src="../../../assets/img/arrow-l.png"></span>
          </li>
        </div>
        <div class="{{fav?'': 'animated'}}">
          <li class="page-item ">
            <span (click)="isNext(true)" [routerLink]="['/list/'+(pag.current+1).toString()]" class="page-link arrow-button arrow m-2 {{!fav?'': 'disabled'}}"><img class="arrow-nav"
                src="../../../assets/img/arrow-r.png"></span>
          </li>
        </div>

      </ul>
    </nav>
  </div>
  <div class="col-sm-6">
    <nav class="d-flex justify-content-end">
      <ul class="pagination pagination-sm">
        <div class="animated">
          <li class="page-item">
            <span (click)="switchViews()" class="page-link img-button arrow " href="#" tabindex="-1"><img class="img-button-nav"
                src="../../../assets/img/list-button-black.png"></span>
          </li>
        </div>
        <div class="animated">

          <li class="page-item">
            <span (click)="listFav()" class="page-link img-button arrow "><img class="img-button-nav"
                src="{{fav ? '../../../assets/img/star-black.png' :'../../../assets/img/star-white.png'}}"></span>
          </li>
        </div>
        <div class="animated">
          <li class="page-item">
            <span (click)="darkMode(dark)" class="page-link img-button arrow "  tabindex="-1"><img class="img-button-nav"
                src="{{!dark ? '../../../assets/img/sun_white.png' :'../../../assets/img/half-moon_black.png'}}"></span>
          </li>
        </div>


      </ul>
    </nav>
  </div>
</div>
