import { Component } from '@angular/core';

@Component({
  selector: 'app-header-list',
  templateUrl: './header-list.component.html',
  styleUrls: ['./header-list.component.css']
})
export class HeaderListComponent {

}
<div class="container">
  <div class="row">
    <div class="col-md-11 subtitle-header">Hello,welcome to the</div>
  </div>
  <div class="row">
    <div class="col-md-7 title-header">Pokedex</div>
    <div class="col-md-5 image-header-container"><img class="image-header" src="../../../assets/img/pixelPokedex.gif"></div>
  </div>
</div>
